/*!

=========================================================
* Black Dashboard React v1.0.0
=========================================================

* Product Page: https://www.creative-tim.com/product/black-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/black-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";

import {
  Button,
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Row,
  Col,
  Table,
  FormGroup,
  Label,
  Input
} from "reactstrap";

class Liquidations extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      liqActive: [],
    };
  }


  componentDidMount() {
    if (this.props.web3Available === "true") {
      for (var i = 0; i < this.props.liqGroups.length; i++) {
        if (this.props.liqGroups[i] > 0){
          for (var n = 0; n <this.props.liqGroups[i]; n++) {
              this.props.instanceColl.methods._LiqInfo(i, (n + 1)
            ).call().then((result => this.setState({liqActive: result})));
     }}}}
     console.log(this.state.liqActive)
   }


  renderTableGroupData() {
    var rows = [];
    for (var i = 0; i < this.props.liqGroups.length; i++) {
      if (this.props.liqGroups[i] > 0){
        rows[i] = (
          <tr key={i}>
            <td>{i*10} USD</td>
            <td>{this.props.liqGroups[i]} CPs</td>
          </tr>
        )}
    }
    return (rows);
  }



  renderTableActiveLiqData() {
    var rowsActive = [];
    if (this.props.web3Available === "true") {
      for (var i = 0; i < this.props.liqGroups.length; i++) {
        if (this.props.liqGroups[i] > 0){
          for (var n = 0; n <this.props.liqGroups[i]; n++) {
              this.props.instanceColl.methods._LiqInfo(i, (n + 1)
              ).call().then((result => this.state.liqActive.push(result)));

              if (this.state.liqActive.length > 0) {
                console.log(this.state.liqActive.lenght)
              }

              rowsActive[n] = (
                  <tr key={n}>
                      <td>{n+1}</td>
                      <td>{this.state.liqActive[0]}</td>
                      <td>{this.state.liqActive}</td>
                      <td>0,00</td>
                      <td>0,00</td>
                      <td className="text-center">0,00 USD</td>
                      <td>
                      <Button
                        className="btn-link tim-icons icon-simple-remove"
                        color="danger"
                      />
                      </td>
                  </tr>);
          }}}} return (rowsActive);
  }
  render() {
    return (
      <>
        <div className="content">
        <Row>
        <Col md="12">
          <Card>
            <CardHeader>
              <CardTitle tag="h4">Active Liquidation Positions</CardTitle>
            </CardHeader>
            <Col md="12">
            <CardBody>
              <Table className="tablesorter" responsive>
                <thead className="text-primary">
                  <tr>
                    <th>ID</th>
                    <th>Owner</th>
                    <th>Owners ID</th>
                    <th>Tokens</th>
                    <th>Collateral</th>
                    <th className="text-center">Liquidation rate</th>
                  </tr>
                </thead>
                <tbody>
                {this.renderTableActiveLiqData()}
                </tbody>
              </Table>
              Current rate: {(this.props.rateUSD/10**2).toLocaleString(
                          undefined, {minimumFractionDigits: 2,
                                      maximumFractionDigits:2})} USD
            </CardBody>
          </Col>
          </Card>
        </Col>
        </Row>

        <Row>
        <Col lg="6" md="12">
          <Card>
            <CardHeader>
              <CardTitle tag="h4">Liquidation groups</CardTitle>
              <FormGroup check>
                  <Label check>
                      <Input
                      type="checkbox"
                      onClick = {this.filterCP}/>
                          <span className="form-check-sign" />
                          Show zero groups
                  </Label>
              </FormGroup>
            </CardHeader>
              <CardBody>
              <Table className="tablesorter" responsive>
                <thead className="text-primary">
                  <tr>
                    <th>Liquidation group</th>
                    <th>Number</th>
                  </tr>
                </thead>
                <tbody>
                  {this.renderTableGroupData()}
                </tbody>
              </Table>
              </CardBody>
          </Card>
        </Col>
        </Row>
        </div>
      </>
    );
  }
}

export default Liquidations;
