* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";

import {
  Button,
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Row,
  Col,
  Table,
} from "reactstrap";

class Liquidations extends React.Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.state = {
      liqActive: [],
    };
  }


  componentDidMount() {
    this._isMounted = true;
    var data = [];

    if (this._isMounted) {
    if (this.props.web3Available === "true") {
      for (var i = 0; i < this.props.liqGroups.length; i++) {
        if (this.props.liqGroups[i] > 0){
          for (var n = 0; n < this.props.liqGroups[i]; n++) {
            this.props.instanceColl.methods._LiqInfo(i, (n + 1)
              ).call().then((result =>


              this.setState({liqActive:
                  this.state.liqActive.concat(result)
              })


              ))}}}}}
   }
   componentWillUnmount() {
     this._isMounted = false;
   }


  renderTableGroupData() {
    var rows = [];
    for (var i = 0; i < this.props.liqGroups.length; i++) {
      if (this.props.liqGroups[i] > 0){
        rows[i] = (
          <tr key={i}>
            <td>{i*10} USD</td>
            <td>{this.props.liqGroups[i]} CPs</td>
          </tr>
        )}
    }
    return (rows);
  }
  renderTableActiveLiqData() {
    var rowsActive = [];
    var index = 0;
      for (var i = 0; i < this.props.liqGroups.length; i++) {
        if (this.props.liqGroups[i] > 0){
          index = index + (this.props.liqGroups[i] * 1);
        }
      }
      for (var n = 0; n < index; n++) {
              rowsActive[n] = (
                  <tr key={n}>
                      <td>{n+1}</td>
                      {this.state.liqActive[n] !== undefined ?
                        (<td>{this.state.liqActive[n][1]}</td>):(<td></td>)}

                      {this.state.liqActive[n] !== undefined ?
                        (<td>{(this.state.liqActive[n][0] *1) + 1}</td>):(<td></td>)}

                      <td>0,00</td>
                      <td>0,00</td>
                      <td className="text-center">0,00 USD</td>
                      <td>
                      <Button
                        className="btn-link tim-icons icon-simple-remove"
                        color="danger"
                      />
                      </td>
                  </tr>);
        } return (rowsActive);
  }
  render() {

    console.log(this.state.liqActive);

    return (
      <>
        <div className="content">
        <Row>
        <Col md="12">
          <Card>
            <CardHeader>
              <CardTitle tag="h4">Active Liquidation Positions</CardTitle>
            </CardHeader>
            <Col md="12">
            <CardBody>
              <Table className="tablesorter" responsive>
                <thead className="text-primary">
                  <tr>
                    <th>ID</th>
                    <th>Owner</th>
                    <th>Owners ID</th>
                    <th>Tokens</th>
                    <th>Collateral</th>
                    <th className="text-center">Liquidation rate</th>
                  </tr>
                </thead>
                <tbody>
                {this.renderTableActiveLiqData()}
                </tbody>
              </Table>
              Current rate: {(this.props.rateUSD/10**2).toLocaleString(
                          undefined, {minimumFractionDigits: 2,
                                      maximumFractionDigits:2})} USD
            </CardBody>
          </Col>
          </Card>
        </Col>
        </Row>
        <Row>
        <Col lg="6" md="12">
          <Card>
            <CardHeader>
              <CardTitle tag="h4">Liquidation groups</CardTitle>
            </CardHeader>
              <CardBody>
              <Table className="tablesorter" responsive>
                <thead className="text-primary">
                  <tr>
                    <th>Liquidation amount</th>
                    <th>Total CPs</th>
                  </tr>
                </thead>
                <tbody>
                  {this.renderTableGroupData()}
                </tbody>
              </Table>
              </CardBody>
          </Card>
        </Col>
        </Row>
        </div>
      </>
    );
  }
}

export default Liquidations;
