*/
import React from "react";

import {
  Button,
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Row,
  Col,
  Table,
  FormGroup,
  Label,
  Input
} from "reactstrap";

class Liquidations extends React.Component {
  renderTableGroupData() {
    var rows = [];
    for (var i = 0; i < this.props.liqGroups.length; i++) {
      if (this.props.liqGroups[i] > 0){
        rows[i] = (
          <tr key={i}>
            <td>{i*10} USD</td>
            <td>{this.props.liqGroups[i]} CPs</td>
          </tr>
        )}
    }
    return (rows);
  }
  renderTableActiveLiqData() {
    var activeLiqCPs = [];
    var rowsActive = [];
    if (this.props.web3Available === "true") {
      for (var i = 0; i < this.props.liqGroups.length; i++) {
        if (this.props.liqGroups[i] > 0){
          var data = [i, this.props.liqGroups[i]];
          activeLiqCPs.push(data);
        }
      console.log(activeLiqCPs);




